proportion_sentiment.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    return sum(values);
  } else {
    return values.length;
  }
}

proportion_sentiment.map:
function(doc) {
  if (doc.RUwar === true) {
    var sentiment = doc.sentiment;
    var sentimentCategory;
  	if (sentiment < 0) {
  	    sentimentCategory = "Negative";
  	  } else if (sentiment === 0) {
  	    sentimentCategory = "Neutral";
  	  } else {
  	    sentimentCategory = "Positive";
  	  }
  	emit(sentimentCategory, 1);
    }
}

magnitude_sentiment_lang.reduce:
function (keys, values, rereduce) {
  var result = {
    count: 0,
    sentiment: 0
  };
  for (var i = 0; i < values.length; i++) {
    result.count += values[i].count;
    result.sentiment += values[i].sentiment;
  }
  return {
    count: result.count,
    sentiment: result.sentiment,
    average_magnitude: result.sentiment/result.count,
  };
}

magnitude_sentiment_lang.map:
function(doc) {
  if (doc.RUwar === true) {
    var sentiment = doc.sentiment < 0 ? -doc.sentiment : doc.sentiment;
  	var language = doc.language;
  	emit({language:language}, {count: 1, sentiment: sentiment});
    }
}

proportion_sentiment_lang.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    return sum(values);
  } else {
    return values.length;
  }
}

proportion_sentiment_lang.map:
function(doc) {
  if (doc.RUwar === true) {
    var sentiment = doc.sentiment;
    var language = doc.language;
    var sentimentCategory;
  	if (sentiment < 0) {
  	    sentimentCategory = "Negative";
  	  } else if (sentiment === 0) {
  	    sentimentCategory = "Neutral";
  	  } else {
  	    sentimentCategory = "Positive";
  	  }
  	emit([language, sentimentCategory], {count:1, sentiment:doc.sentiment});
    }
}
