RUwar.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    return sum(values);
  } else {
    return values.length;
  }
}

RUwar.map:
function(doc) {
  if (doc.RUwar === true) {
    var sentiment = doc.sentiment;
    var sentimentCategory;
  	if (sentiment < 0) {
  	    sentimentCategory = "Negative";
  	  } else if (sentiment === 0) {
  	    sentimentCategory = "Neutral";
  	  } else {
  	    sentimentCategory = "Positive";
  	  }
  	emit(sentimentCategory, 1);
    }
}

