ave_sent_postcode.reduce:
function (keys, values, rereduce) {
  var result = {
    count: 0,
    sentiment: 0
  };

  for (var i = 0; i < values.length; i++) {
    result.count += values[i].count;
    result.sentiment += values[i].sentiment;
  }

  return {
    count: result.count,
    sentiment: result.sentiment / result.count
  };
}

ave_sent_postcode.map:
function (doc) {
  if (doc.RUwar === true && doc.postcode) {
    emit(doc.postcode, {
      count: 1,
      sentiment: doc.sentiment
    });
  }
}

war_monthly_state_proportion.reduce:
function (keys, values, rereduce) {
  var result = {
    count: 0,
    sentiment: 0
  };
  for (var i = 0; i < values.length; i++) {
    result.count += values[i].count;
    result.sentiment += Math.abs(values[i].sentiment);
  }
  return {
    count: result.count,
    sentiment: result.sentiment,
    average_magnitude: result.sentiment/result.count,
  };
}

war_monthly_state_proportion.map:
function(doc) {
  if (doc.RUwar === true) {
    var sentiment = doc.sentiment;
    var date = doc.date;
    var sentimentCategory;
    var state = doc.state;
  	if (sentiment < 0) {
  	    sentimentCategory = "Negative";
  	  } else if (sentiment === 0) {
  	    sentimentCategory = "Neutral";
  	  } else {
  	    sentimentCategory = "Positive";
  	  }
  	emit([date, state, sentimentCategory], {count:1, sentiment:doc.sentiment});
    }
}

